mapper.select:
SELECT I.PRODUCTID, I.ITEMID, I.USERID, I.TITLE, I.QTY, I.UNITCOST, I.DESCRIPTION, I.VIEWCOUNT, LISTPRICE
FROM ITEM I, HANDMADE H
WHERE H.ITEMID = I.ITEMID AND I.ITEMID = #{itemId}

mapper.update:
  	UPDATE HANDMADE SET
	  	ITEMID = #{itemId},
	  	LISTPRICE = #{listPrice}
  	WHERE ITEMID = #{itemId}
